import { initializeApp } from 'firebase/app';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow
} from '@material-ui/core';
import {
  getFirestore, doc, getDocs, query, where
} from 'firebase/firestore';

const getSamples = () => {
  const firebaseConfig = initializeApp({
    apiKey: 'AIzaSyC64KY9UehoX2fk7Ugw2XNPvG4zZ7sSdsQ',
    authDomain: 'uno-genomics.firebaseapp.com',
    databaseURL: 'https://uno-genomics-default-rtdb.firebaseio.com',
    projectId: 'uno-genomics',
    storageBucket: 'uno-genomics.appspot.com',
    messagingSenderId: '351603848354',
    appId: '1:351603848354:web:e974a024da6b7e7472d3fb'
  });
  const db = getFirestore(firebaseConfig);

  if (localStorage.getItem("adminMode") == "true") {
    const samples = getDoc(doc(db, "samples"), "samples", "samples");
    this.setState({
      samples: samples
    });
  } else {
    const q = query(doc(db, "samples"), where("userID", "==", localStorage.getItem("userID")));
    const samples = getDocs(q);
    this.setState({
      samples: samples
    });
  } 
}

const Samples = () => (
  <TableContainer>
    <Table>
      <TableHead>
        <TableRow>
          <TableCell>Sample Name</TableCell>
          <TableCell>Date Submitted</TableCell>
          <TableCell>Stage</TableCell>
          <TableCell>Estimated Time of Completion</TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        { getSamples.forEach((sample) => (
          <TableRow>
            <TableCell>{sample.name}</TableCell>
            <TableCell>{sample.submissionDate}</TableCell>
            <TableCell>{sample.stage}</TableCell>
            <TableCell>{sample.eta}</TableCell>
          </TableRow>
        ))}
      </TableBody>
    </Table>
  </TableContainer>
);

export default Samples;
