import { Link as RouterLink } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import {
  Box, Container, Typography, TextField, Button, Link
} from '@material-ui/core';
import { useFormik } from 'formik';
import { Navigate } from 'react-router';

const validate = (values) => {
  const errors = {};
  if (!values.email) {
    errors.email = 'Required';
  }
  if (!values.password) {
    errors.password = 'Required';
  } else if (values.password.length < 8) {
    errors.password = 'Must be at least 8 characters';
  }

  return errors;
};

const Login = () => {
  const formik = useFormik({
    initialValues: {
      email: 'email',
      password: 'password'
    },
    validate,
    onSubmit: () => {
      Navigate('/dashboard', { replace: true });
    },
  });

  return (
    <>
      <Helmet>
        <title>UNO COVID Resources Collection</title>
      </Helmet>
      <Box
        sx={{
          backgroundColor: 'background.default',
          display: 'flex',
          flexDirection: 'column',
          height: '100%',
          justifyContent: 'center'
        }}
      >
        <Container maxWidth="sm">
          <form onSubmit={formik.handleSubmit}>
            <Box sx={{ mb: 3 }}>
              <Typography color="textPrimary" variant="h2"> Sign in </Typography>
              <Typography color="textSecondary" gutterBottom variant="body2"> Sign in on the internal platform </Typography>
            </Box>
            <Box sx={{ pb: 1, pt: 3 }}>
              <Typography align="center" color="textSecondary" variant="body1"> Login with email address </Typography>
            </Box>
            <TextField error={Boolean(formik.touched.email && formik.errors.email)} fullWidth helperText={formik.touched.email && formik.errors.email} label="email" margin="normal" name="email" onBlur={formik.handleBlur} onChange={formik.handleChange} type="email" value={formik.values.email} variant="outlined" />
            <TextField error={Boolean(formik.touched.password && formik.errors.password)} fullWidth helperText={formik.touched.password && formik.errors.password} label="Password" margin="normal" name="password" onBlur={formik.handleBlur} onChange={formik.handleChange} type="password" value={formik.values.password} variant="outlined" />
            <Box sx={{ py: 2 }}>
              <Button color="primary" disabled={formik.isSubmitting} fullWidth size="large" type="submit" variant="contained"> Sign in now </Button>
            </Box>
            <Typography color="textSecondary" variant="body1">
              Don&apos;t have an account?
              {' '} 
              <Link component={RouterLink} to="/register" variant="h6" underline="hover"> Sign up </Link></Typography>
          </form>
        </Container>
      </Box>
    </>
  );
};

export default Login;
