import { Link as RouterLink } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { useFormik } from 'formik';
import {
  Box, Button, Checkbox, Container, FormHelperText, Link, TextField, Typography
} from '@material-ui/core';
import { Navigate } from 'react-router';

const Register = () => {
  const validate = (values) => {
    const errors = {};
    if (!values.email) {
      errors.email = 'Required';
    }
    if (!values.password) {
      errors.password = 'Required';
    } else if (values.password.length < 8) {
      errors.password = 'Must be at least 8 characters';
    }
    return errors;
  };

  const formik = useFormik({
    initialValues: {
      email: 'email',
      password: 'password'
    },
    validate,
    onSubmit: () => {
      Navigate('/dashboard', { replace: true });
    },
  });

  return (
    <>
      <Helmet>
        <title> UNO COVID Resources Collection </title>
      </Helmet>
      <Box sx={{
        backgroundColor: 'background.default', display: 'flex', flexDirection: 'column', height: '100%', justifyContent: 'center'
      }}
      >
        <Container maxWidth="sm">
          <form onSubmit={formik.handleSubmit}>
            <Box sx={{ mb: 3 }}>
              <Typography color="textSecondary" variant="h2"> Create new account </Typography>
              <Typography color="textSecondary" gutterBottom variant="body2"> Use your email to create new account</Typography>
            </Box>
            <TextField InputProps={{ color: 'secondary' }} error={Boolean(formik.touched.firstName && formik.errors.firstName)} fullWidth helperText={formik.touched.firstName && formik.errors.firstName} label="First name" margin="normal" name="firstName" onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.firstName} variant="outlined" />
            <TextField error={Boolean(formik.touched.lastName && formik.errors.lastName)} fullWidth helperText={formik.touched.lastName && formik.errors.lastName} label="Last name" margin="normal" name="lastName" onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.lastName} variant="outlined" />
            <TextField error={Boolean(formik.touched.email && formik.errors.email)} fullWidth helperText={formik.touched.email && formik.errors.email} label="Email Address" margin="normal" name="email" onBlur={formik.handleBlur} onChange={formik.handleChange} type="email" value={formik.values.email} variant="outlined" />
            <TextField error={Boolean(formik.touched.password && formik.errors.password)} fullWidth helperText={formik.touched.password && formik.errors.password} label="Password" margin="normal" name="password" onBlur={formik.handleBlur} onChange={formik.handleChange} type="password" value={formik.values.password} variant="outlined" />
            <Box sx={{ alignItems: 'center', display: 'flex', ml: -1 }}>
              <Checkbox checked={formik.values.policy} name="policy" onChange={formik.handleChange} />
              <Typography color="textSecondary" variant="body1">
                I have read the
                {' '}
                <Link color="primary" component={RouterLink} to="#" underline="always" variant="h6"> Terms and Conditions </Link>
              </Typography>
            </Box>
            {Boolean(formik.touched.policy && formik.errors.policy) && (
            <FormHelperText error>
              {formik.errors.policy}
            </FormHelperText>
            )}
            <Box sx={{ py: 2 }}>
              <Button color="primary" disabled={formik.isSubmitting} fullWidth size="large" type="submit" variant="contained"> Sign up now </Button>
            </Box>
            <Typography color="textSecondary" variant="body1">
              Have an account?
              {' '}
              <Link component={RouterLink} to="/login" variant="h6" underline="hover">
                Sign in
              </Link>
            </Typography>
          </form>
        </Container>
      </Box>
    </>
  );
};

export default Register;
