import { Link as RouterLink } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import {
  Box, Container, Typography, TextField, Button, Link
} from '@material-ui/core';
import { useFormik } from 'formik';
import { Navigate } from 'react-router';
import { collection, getDocs, query, where } from 'firebase/firestore';

const initializeDB = () => {
  const firebaseConfig = initializeApp({
    apiKey: 'AIzaSyC64KY9UehoX2fk7Ugw2XNPvG4zZ7sSdsQ',
    authDomain: 'uno-genomics.firebaseapp.com',
    databaseURL: 'https://uno-genomics-default-rtdb.firebaseio.com',
    projectId: 'uno-genomics',
    storageBucket: 'uno-genomics.appspot.com',
    messagingSenderId: '351603848354',
    appId: '1:351603848354:web:e974a024da6b7e7472d3fb'
  });
  return getFirestore();
};

const login = (db, email, password) => {
  const docsRef = collection
  q = query()
};

const Login = () => {
  const validate = (values) => {
    const errors = {};
    if (!values.email) {
      errors.email = 'Required';
    }
    if (!values.password) {
      errors.password = 'Required';
    } else if (values.password.length < 8) {
      errors.password = 'Must be at least 8 characters';
    }
    return errors;
  };

  const formik = useFormik({
    initialValues: {
      email: 'email',
      password: 'password'
    },
    validate,
    onSubmit: () => {
      const db = initializeDB();
      login(db, values.email, values.password);

      Navigate('/dashboard', { replace: true });
    },
  });

  return (
    <>
      <Helmet>
        <title>UNO COVID Resources Collection</title>
      </Helmet>
      <Box
        sx={{
          backgroundColor: 'background.default',
          display: 'flex',
          flexDirection: 'column',
          height: '100%',
          justifyContent: 'center'
        }}
      >
        <Container maxWidth="sm">
          <form onSubmit={formik.handleSubmit}>
            <Box sx={{ mb: 3 }}>
              <Typography color="textPrimary" variant="h2"> Sign in </Typography>
            </Box>
            <TextField error={Boolean(formik.touched.email && formik.errors.email)} fullWidth helperText={formik.touched.email && formik.errors.email} label="email" margin="normal" name="email" onBlur={formik.handleBlur} onChange={formik.handleChange} type="email" value={formik.values.email} variant="outlined" />
            <TextField error={Boolean(formik.touched.password && formik.errors.password)} fullWidth helperText={formik.touched.password && formik.errors.password} label="Password" margin="normal" name="password" onBlur={formik.handleBlur} onChange={formik.handleChange} type="password" value={formik.values.password} variant="outlined" />
            <Box sx={{ py: 2 }}>
              <Button color="primary" disabled={formik.isSubmitting} fullWidth size="large" type="submit" variant="contained"> Sign in now </Button>
            </Box>
            <Typography color="textSecondary" variant="body1">
              Don&apos;t have an account?
              {' '}
              <Link component={RouterLink} to="/register" variant="h6" underline="hover" user_id={user_id}>
                Sign up
              </Link>
            </Typography>
          </form>
        </Container>
      </Box>
    </>
  );
};

export default Login;
