import { Link as RouterLink } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import * as Yup from 'yup';
import {
  Box, Container, Typography, TextField, Button, Link
} from '@material-ui/core';
import { useFormik } from 'formik';
import { Navigate } from 'react-router';
import { initializeApp } from 'firebase/app';
import {
  getFirestore, collection, getDocs, query, where
} from 'firebase/firestore';
import { Component } from 'react';

const initializeDB = () => {
  initializeApp({
    apiKey: 'AIzaSyC64KY9UehoX2fk7Ugw2XNPvG4zZ7sSdsQ',
    authDomain: 'uno-genomics.firebaseapp.com',
    databaseURL: 'https://uno-genomics-default-rtdb.firebaseio.com',
    projectId: 'uno-genomics',
    storageBucket: 'uno-genomics.appspot.com',
    messagingSenderId: '351603848354',
    appId: '1:351603848354:web:e974a024da6b7e7472d3fb'
  });
  return getFirestore();
};

const login = async (db, email, password) => {
  const docsRef = collection(db, 'clients');
  const q1 = query(docsRef, where('email', '==', email));
  const q2 = query(docsRef, where('password', '==', password));
  const querySnapshot1 = await getDocs(q1);
  const querySnapshot2 = await getDocs(q2);

  if (querySnapshot1.length === 1 && querySnapshot2.length > 0) {
    for (let i = 0; i < querySnapshot1.length; i++) {
      if (email === querySnapshot1[0].email) {
        return querySnapshot1[0].email;
      }
    }
  }
  return '';
};

const validate = (values) => {
  const errors = {};
  if (!values.email) {
    errors.email = 'Required';
  }
  if (!values.password) {
    errors.password = 'Required';
  } else if (values.password.length < 8) {
    errors.password = 'Must be at least 8 characters';
  }
  return errors;
};

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = { failedLogin: false };
  }

  render() {
    const { failedLogin } = this.state;
    const formik = useFormik({
      initialValues: {
        email: 'email',
        password: 'password'
      },
      validate,
      onSubmit: (values) => {
        
      },
    });
    return (
      <>
        <Helmet>
          <title>UNO COVID Resources Collection</title>
        </Helmet>
        <Box
          sx={{
            backgroundColor: 'background.default',
            display: 'flex',
            flexDirection: 'column',
            height: '100%',
            justifyContent: 'center'
          }}
        >
          <Container maxWidth="sm">
            <Formik 
              initialValues={{ email: 'email', password: 'password' }} 
              validationSchema={Yup.object().shape({ email: Yup.string().email('Must be a valid email').max(255).required('Email is required'), password: Yup.string().max(255).required('Password is required') })} 
              onSubmit={() => {
                const db = initializeDB();
                const userID = login(db, values.email, values.password);
                if (userID !== '') {
                  localStorage.setItem('userID', userID);
                  Navigate('/dashboard', { replace: true });
                } else {
                  this.setState({
                    failedLogin: true
                  });
                }
              }}>
                {({ errors, handleBlur, handleChange, handleSubmit, isSubmitting, touched, values })} =>  (
                  <form onSubmit={handleSubmit}>
                    <Box sx={{ mb: 3 }}>
                      <Typography color="textPrimary" variant="h2"> Sign in </Typography>
                    </Box>
                    <TextField error={Boolean(touched.email && errors.email)} fullWidth helperText={formik.touched.email && formik.errors.email} label="email" margin="normal" name="email" onBlur={formik.handleBlur} onChange={formik.handleChange} type="email" value={formik.values.email} variant="outlined" />
                    <TextField error={Boolean(touched.password && errors.password)} fullWidth helperText={formik.touched.password && formik.errors.password} label="Password" margin="normal" name="password" onBlur={formik.handleBlur} onChange={formik.handleChange} type="password" value={formik.values.password} variant="outlined" />
                    <Box sx={{ py: 2 }}>
                      <Button color="primary" disabled={isSubmitting} fullWidth size="large" type="submit" variant="contained"> Sign in now </Button>
                    </Box>
                    {failedLogin && <Typography color="textPrimary" variant="body1"> Username and Password combination unknown. Try again. </Typography>}
                    <Typography color="textSecondary" variant="body1">
                      Don&apos;t have an account?
                      {' '}
                      <Link component={RouterLink} to="/register" variant="h6" underline="hover">
                        Sign up
                      </Link>
                    </Typography>
                  </form>
                )
           </Formik>
          </Container>
        </Box>
      </>
    );
  }
}

export default Login;
