import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { Formik } from 'formik';
import {
  Box, Button, Checkbox, Container, FormHelperText, Link, TextField, Typography
} from '@material-ui/core';

const Register = () => {
  const formik = useFormik({
    initialValues: {
      email: 'email',
      password: 'password'
    },
    validate,
    onSubmit: () => {
      Navigate('/dashboard', { replace: true });
    },
  });

  return (
    <>
      <Helmet>
        <title> UNO COVID Resources Collection </title>
      </Helmet>
      <Box sx={{ backgroundColor: 'background.default', display: 'flex', flexDirection: 'column', height: '100%', justifyContent: 'center' }}>
        <Container maxWidth="sm">
          <form onSubmit={handleSubmit}>
            <Box sx={{ mb: 3 }}>
              <Typography color="textPrimary" variant="h2"> Create new account </Typography>
              <Typography color="textSecondary" gutterBottom variant="body2"> Use your email to create new account</Typography>
            </Box>
            <TextField error={Boolean(formik.touched.firstName && formik.errors.firstName)} fullWidth helperText={formik.touched.firstName && formik.errors.firstName} label="First name" margin="normal" name="firstName" onBlur={handleBlur} onChange={handleChange} value={values.firstName} variant="outlined"/>
            <TextField error={Boolean(formik.touched.lastName && formik.errors.lastName)} fullWidth helperText={formik.touched.lastName && formik.errors.lastName} label="Last name" margin="normal" name="lastName" onBlur={formik.handleBlur} onChange={formik.handleChange} value={formik.values.lastName} variant="outlined"/>
            <TextField
              error={Boolean(touched.email && errors.email)}
              fullWidth
              helperText={touched.email && errors.email}
              label="Email Address"
              margin="normal"
              name="email"
              onBlur={handleBlur}
              onChange={handleChange}
              type="email"
              value={values.email}
              variant="outlined"
            />
            <TextField
              error={Boolean(touched.password && errors.password)}
              fullWidth
              helperText={touched.password && errors.password}
              label="Password"
              margin="normal"
              name="password"
              onBlur={handleBlur}
              onChange={handleChange}
              type="password"
              value={values.password}
              variant="outlined"
            />
            <Box
              sx={{
                alignItems: 'center',
                display: 'flex',
                ml: -1
              }}
            >
              <Checkbox
                checked={values.policy}
                name="policy"
                onChange={handleChange}
              />
              <Typography
                color="textSecondary"
                variant="body1"
              >
                I have read the
                {' '}
                <Link
                  color="primary"
                  component={RouterLink}
                  to="#"
                  underline="always"
                  variant="h6"
                >
                  Terms and Conditions
                </Link>
              </Typography>
            </Box>
            {Boolean(touched.policy && errors.policy) && (
            <FormHelperText error>
              {errors.policy}
            </FormHelperText>
            )}
            <Box sx={{ py: 2 }}>
              <Button
                color="primary"
                disabled={isSubmitting}
                fullWidth
                size="large"
                type="submit"
                variant="contained"
              >
                Sign up now
              </Button>
            </Box>
            <Typography
              color="textSecondary"
              variant="body1"
            >
              Have an account?
              {' '}
              <Link component={RouterLink} to="/login" variant="h6" underline="hover">
                Sign in
              </Link>
            </Typography>
          </form>
        </Container>
      </Box>
    </>
  )
};

export default Register;
